import { useState } from "react";

const Example = () => {
  let valArray = useState();

  return (
    <>
      <input
      type="text"
      onChange={(e) => {
        const setFunc = valArray[1];
        setFunc(e.target.value)
        // displayVal = e.target.value
      }} /> = {valArray[0]}
    </>
  );
};

export default Example;
