let outsideCounter = 0;

const Example = () => {
    const [counter, setCounter] = useState(0);

    function increment() {
        outsideCounter++;
        setCounter(counter + 1);
    }

    return (
        <div>
            <p>Counter: {counter}</p>
            <p>Outside Counter: {outsideCounter}</p>
            <button onClick={increment}>Increment</button>
        </div>
    );
}
export default Example;
